import { Component } from 'react'
import Cart from "../components/cart"
import Item from "../components/item"
import MenuBar from "../components/menubar"

export default class Page extends Component {
	state = {
		cartOpen: false,
		itemMap: {}
	}

	toggleCart = () => this.setState({ cartOpen: !this.state.cartOpen})

	addToCart = (item) => {
		let newItemMap = this.state.itemMap;
		newItemMap[item.id] = item
		this.setState({ itemMap: newItemMap });
		console.log(this.state.itemMap)
	}

	render() {
		const { items } = this.props
		const { cartOpen, itemMap } = this.state

		return (
			<div className="page">
				<MenuBar toggleCart={this.toggleCart} />
				<div className="fake-menubar" />
				<div className="page-content">
					{ cartOpen && <Cart itemMap />}
				    <h1>Accessories & Parts</h1>
				    {
				    	items.map((item, i) => (
				            <Item item={item} added={!!itemMap[item.id]} addToCart={this.addToCart} />
				  		))
			    	}
			    </div>
			</div>
		)
	}
}
