import { Component } from 'react'
import { Dropdown } from 'react-bootstrap'

import Cart from "../components/cart"
import Item from "../components/item"
import MenuBar from "../components/menubar"

export default class Page extends Component {
	state = {
		cartOpen: false,
		itemMap: {},
		assetDescription: "",
		assetManufacturer: "",
		assetModel: "",
		partType: "",
		partDescription: "",
		items: this.props.items
	}

	toggleCart = () => this.setState({ cartOpen: !this.state.cartOpen})

	addToCart = (item) => {
		let newItemMap = this.state.itemMap;
		newItemMap[item.id] = item
		this.setState({ itemMap: newItemMap });
		console.log(this.state.itemMap)
	}

	// TODO: Generalize to all property names
	filterAssetDescription = (items, value) => items.filter(x => x.asset_description === value)

	filterAssetManufacturer = (items, value)  => tems.filter(x => x.asset_manufacturer === value)

	filterAssetModel = (items, value)  => items.filter(x => x.asset_model === value)

	filterPartType = (items, value)  => items.filter(x => x.part_type === value)

	filterPartDescription = (items, value)  => items.filter(x => x.part_description === value)

	getAssetDescriptions = (items, value) => Array(Set(items.map(item => item.asset_description)))

	getAssetManufacturers = (items, value) => (
		Array(Set(items.map(item => item.asset_manufacturer)))
	)
	getAssetModels = (items, value) => (
		Array(Set(items.map(item => item.asset_model)))
	)
	getPartType = (items, value) => (
		Array(Set(items.map(item => item.part_type)))
	)
	getPartDescription = (items, value) => (
		Array(Set(items.map(item => item.part_description)))
	) 

	hadleAssetDescriptionSelect = (value) => {
		let { nextItems: [...items] } = this.state
		this.setState({ 
			items: this.filterAssetDescription(nextItems, value),
			assetManufacturer: '',
			assetModel: '',
			partType: '',
			partModel: ''
		})
	}

	hadleAssetManufacturerSelect = (value) => {
		let { nextItems: [...items] } = this.state
		this.setState({ 
			items: this.filterAssetManufacturer(nextItems, value),
			assetModel: '',
			partType: '',
			partModel: '' 
		})
	}

	hadleAssetModelSelect = (value) => {
		let { nextItems: [...items] } = this.state
		this.setState({ 
			items: this.filterAssetModel(nextItems, value),
			partType: '',
			partModel: ''
		})
	}

	handlePartType = (value) => {
		let { nextItems: [...items] } = this.state
		this.setState({ 
			items: this.filterPartType(nextItems, value),
			partModel: ''
		})
	}

	handlePartDescription = () => {
		let { nextItems: [...items] } = this.state
		this.setState({ 
			items: this.filterPartDescription(nextItems, value) 
		})
	}


	render() {
		const { cartOpen, itemMap, items } = this.state

		return (
			<div className="page">
				<MenuBar toggleCart={this.toggleCart} />
				<div className="fake-menubar" />
				<div className="page-content">
					{ cartOpen && <Cart itemMap={itemMap} />}
					<Dropdown>
					  <Dropdown.Toggle variant="success" id="dropdown-basic" className="dropdown-button">
					   	Part Description
					  </Dropdown.Toggle>

					  <Dropdown.Menu className="dropdown-menu">
					    <Dropdown.Item href="#/action-1" className="dropdown-item">Action</Dropdown.Item>
					    <Dropdown.Item href="#/action-2" className="dropdown-item">Another action</Dropdown.Item>
					    <Dropdown.Item href="#/action-3" className="dropdown-menu">Something else</Dropdown.Item>
					  </Dropdown.Menu>
					</Dropdown>
				    <h1>Accessories & Parts</h1>
				    {
				    	items.map((item, i) => (
				            <Item item={item} added={!!itemMap[item.id]} addToCart={this.addToCart} />
				  		))
			    	}
			    </div>
			</div>
		)
	}
}
